#pragma config(Sensor, dgtl1,  pin1,           sensorTouch)
#pragma config(Sensor, dgtl2,  pin2,           sensorTouch)
#pragma config(Motor,  port1,           leftMotor,     tmotorServoContinuousRotation, openLoop, reversed)
#pragma config(Motor,  port10,          rightMotor,    tmotorServoContinuousRotation, openLoop)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

#pragma platform(VEX)

//Competition Control and Duration Settings
#pragma competitionControl(Competition)
#pragma autonomousDuration(20)
#pragma userControlDuration(120)

#include "Vex_Competition_Includes.c"   //Main competition background code...do not modify!

int speed();
int accelP();
int accelN();
void stopMotors();
void subname();
void tankControl();

void accelerometerControl();

int threshold = 20;   // helps to eliminate 'noise' from a joystick that isn't perfectly at (0,0)
int accel_x   = 0;      // Will hold the controller-accelerometer X reading.
int accel_y   = 0;      // Will hold the controller-accelerometer Y reading.
int prev_x    = 0;      // Will hold the previous accelerometer X reading (for comparison).
int prev_y    = 0;      // Will hold the previous accelerometer Y reading (for comparison).
int joy_x;            // will hold the X value of the analog stick (choices below)
int joy_y;            // will hold the Y value of the analog stick (choices below)
